import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../App.css';
import sampleImg from '../../assets/damon.jpg';

const Playlist = () => {
  return (
    <div className="container mt-4">
      <div className="d-flex align-items-center">
        <img src={sampleImg} alt="user" className="playlist-image mr-2" />
        <div className="d-flex flex-column">
          <span>Songs to listen while coding</span>
          <span className="text-secondary">
            <small>by Damon Joung</small>
          </span>
        </div>
        <button className="btn btn-danger h-25 ml-auto px-2 py-1">Follow</button>
      </div>
    </div>
  );
};

export default Playlist;
